mapper.insert:
INSERT INTO t_department(
	name, 
	sort
	
	,parent_id
	
) VALUES (
	#{name}, 
	#{sort}
	
	,#{parent.id}
	
)

mapper.update:
UPDATE t_department SET
name=#{name},
sort=#{sort}

,parent_id=#{parent.id}

WHERE id=#{id}

mapper.select:
SELECT 
FROM t_department a LEFT JOIN t_department b ON a.parent_id=b.id 
WHERE a.id = #{id}
