mapper.insert:
INSERT INTO t_menu( name, href, permission, type, sort, icon
,parent_id
) VALUES ( #{name}, #{href}, #{permission}, #{type}, #{sort}, #{icon}
,#{parent.id}
)

mapper.update:
UPDATE t_menu SET
name=#{name},
href=#{href},
permission=#{permission},
type=#{type}, 
sort=#{sort},
icon=#{icon}

,parent_id=#{parent.id}

WHERE id=#{id}

mapper.select:
SELECT ,b.name AS "parent.name"
FROM t_menu a LEFT JOIN t_menu b ON a.parent_id=b.id 
WHERE a.id = #{id}
